<span class="line"><span style="color:#8BE9FD">  max-width</span><span style="color:#FF79C6">:</span><span style="color:#BD93F9"> 400</span><span style="color:#FF79C6">px</span><span style="color:#F8F8F2">;              </span><span style="color:#6272A4">/* Ancho máximo */</span></span>
